import React from "react";
import "./App.css";

function App() {
  const ementa = {
    "Segunda-feira": ["Bacalhau √† Br√°s", "Salada de atum"],
    "Ter√ßa-feira": ["Feijoada", "Quiche de legumes"],
    "Quarta-feira": ["Frango assado", "Massa com pesto"],
    "Quinta-feira": ["Arroz de pato", "Tofu grelhado com legumes"],
    "Sexta-feira": ["Sushi variado", "Lasanha vegetariana"],
  };

  return (
    <div className="App">
      <header className="header">
        <h1>üçΩ Restaurante Catarina</h1>
        <p>Bem-vinda ao restaurante Catarina!</p>
        <p>Para esta semana temos como ementa o seguinte:</p>
      </header>
      <div className="ementa">
        {Object.entries(ementa).map(([dia, pratos]) => (
          <div key={dia} className="dia">
            <h2>{dia}:</h2>
            <ul>
              {pratos.map((prato, index) => (
                <li key={index}>‚Üí {prato}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
